      out.write("            checkForNotifications();\r\n");
      out.write("            \r\n");
      out.write("            // Then check every 30 seconds\r\n");
      out.write("            checkInterval = setInterval(checkForNotifications, 30000);\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        // Check for active alarms and reminders\r\n");
      out.write("        function checkForNotifications() {\r\n");
      out.write("        const now = new Date();\r\n");
      out.write("\r\n");
      out.write("        // Mengambil jam dan menit lokal\r\n");
      out.write("        const currentTime = now.getHours().toString().padStart(2, '0') + ':' +\r\n");
      out.write("                            now.getMinutes().toString().padStart(2, '0');\r\n");
      out.write("\r\n");
      out.write("        // Mengambil tanggal lokal dengan metode Date yang lokal\r\n");
      out.write("        const year = now.getFullYear();\r\n");
      out.write("        const month = (now.getMonth() + 1).toString().padStart(2, '0');\r\n");
      out.write("        const day = now.getDate().toString().padStart(2, '0');\r\n");
      out.write("\r\n");
      out.write("        // Log nilai individual sebelum penggabungan\r\n");
      out.write("        console.log(\"Year:\", year);\r\n");
      out.write("        console.log(\"Month:\", month);\r\n");
      out.write("        console.log(\"Day:\", day);\r\n");
      out.write("\r\n");
      out.write("        const currentDate = year + \"-\" + month + \"-\" + day;\r\n");
      out.write("\r\n");
      out.write("        console.log('Checking notifications for:', currentDate, currentTime);\r\n");
      out.write("            \r\n");
      out.write("            // Check for alarms\r\n");
      out.write("            fetch('CheckActiveAlarmsServlet?userId=' + notificationData.userId + '&date=' + currentDate + '&time=' + currentTime)\r\n");
      out.write("                .then(response => response.json())\r\n");
