      out.write("                    console.log('CHECK activeAlarms:', activeAlarms);\r\n");
      out.write("                    console.log('CheckActiveAlarmsServlet?userId=' + notificationData.userId + '&date=' + currentDate + '&time=' + currentTime);\r\n");
      out.write("                })\r\n");
