      out.write("                .then(response => response.json())\r\n");
      out.write("                .then(reminders => {\r\n");
      out.write("                    if (reminders && reminders.length > 0) {\r\n");
      out.write("                        reminders.forEach(reminder => {\r\n");
      out.write("                            if (!activeReminders.includes(reminder.id)) {\r\n");
      out.write("                                showReminderNotification(reminder);\r\n");
      out.write("                                activeReminders.push(reminder.id);\r\n");
      out.write("                            }\r\n");
