      out.write("                    console.log('CHECK activeReminders:', activeReminders);\r\n");
      out.write("                    console.log('CheckActiveRemindersServlet?userId=' + notificationData.userId + '&date=' + currentDate + '&time=' + currentTime);\r\n");
      out.write("                })\r\n");
      out.write("                .catch(error => console.error('Error checking reminders:', error));\r\n");
