      out.write("        \r\n");
      out.write("        // Show alarm notification with sound\r\n");
      out.write("        function showAlarmNotification(alarm) {\r\n");
      out.write("            console.log('Showing alarm notification:', alarm);\r\n");
      out.write("            \r\n");
