      out.write("            }).then((result) => {\r\n");
      out.write("                stopAlarmSound();\r\n");
      out.write("                if (result.isDismissed && result.dismiss === Swal.DismissReason.cancel) {\r\n");
      out.write("                    // Snooze for 5 minutes\r\n");
      out.write("                    snoozeAlarm(alarm.id, 5);\r\n");
      out.write("                } else {\r\n");
