      out.write("        // Snooze alarm\r\n");
      out.write("        function snoozeAlarm(alarmId, minutes) {\r\n");
      out.write("            fetch('SnoozeAlarmServlet', {\r\n");
      out.write("                method: 'POST',\r\n");
      out.write("                headers: {\r\n");
