      out.write("    \r\n");
      out.write("    function showAddModalNote(type = null) {\r\n");
      out.write("        const modal = new bootstrap.Modal(document.getElementById('modaladd'));\r\n");
      out.write("        modal.show();\r\n");
      out.write("        \r\n");
      out.write("        // Reset forms\r\n");
      out.write("        document.getElementById('textNoteTitle').value = '';\r\n");
      out.write("        document.getElementById('textNoteContent').value = '';\r\n");
      out.write("        document.getElementById('voiceNoteTitle').value = '';\r\n");
      out.write("        document.getElementById('voiceNoteFile').value = '';\r\n");
      out.write("        document.getElementById('voiceNoteUrl').value = '';\r\n");
      out.write("        document.getElementById('recordedAudioData').value = '';\r\n");
      out.write("        \r\n");
      out.write("        // Reset recording UI\r\n");
      out.write("        resetRecordingUI();\r\n");
      out.write("        \r\n");
      out.write("        // Switch to specific tab if type is specified\r\n");
      out.write("        if (type === 'text') {\r\n");
      out.write("            document.getElementById('text-tab').click();\r\n");
      out.write("        } else if (type === 'voice') {\r\n");
      out.write("            document.getElementById('voice-tab').click();\r\n");
      out.write("        }\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    function editTextNote(id, title, content) {\r\n");
      out.write("        try {\r\n");
      out.write("            document.getElementById('editTextNoteId').value = id;\r\n");
      out.write("            document.getElementById('editTextNoteTitle').value = title || '';\r\n");
      out.write("            document.getElementById('editTextNoteContent').value = content.replace(/\\\\n/g, '\\n') || '';\r\n");
      out.write("            \r\n");
      out.write("            const modal = new bootstrap.Modal(document.getElementById('editTextNoteModal'));\r\n");
      out.write("            modal.show();\r\n");
      out.write("        } catch (error) {\r\n");
      out.write("            console.error('Error opening text note edit modal:', error);\r\n");
      out.write("            Swal.fire({\r\n");
      out.write("                icon: 'error',\r\n");
      out.write("                title: 'Error',\r\n");
      out.write("                text: 'Error opening edit form. Please try again.'\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    function editVoiceNote(id, title, voiceUrl) {\r\n");
      out.write("        try {\r\n");
      out.write("            document.getElementById('editVoiceNoteId').value = id;\r\n");
      out.write("            document.getElementById('editVoiceNoteTitle').value = title || '';\r\n");
      out.write("            \r\n");
      out.write("            // Set current voice file info\r\n");
      out.write("            if (voiceUrl) {\r\n");
      out.write("                document.getElementById('currentVoiceSource').src = voiceUrl;\r\n");
      out.write("                document.getElementById('currentVoicePlayer').load();\r\n");
      out.write("                document.getElementById('currentFileName').textContent = voiceUrl.split('/').pop();\r\n");
      out.write("                document.getElementById('editVoiceUrl').value = voiceUrl;\r\n");
      out.write("            } else {\r\n");
      out.write("                document.getElementById('currentFileName').textContent = 'No file';\r\n");
      out.write("                document.getElementById('editVoiceUrl').value = '';\r\n");
      out.write("            }\r\n");
      out.write("            \r\n");
      out.write("            const modal = new bootstrap.Modal(document.getElementById('editVoiceNoteModal'));\r\n");
      out.write("            modal.show();\r\n");
      out.write("        } catch (error) {\r\n");
      out.write("            console.error('Error opening voice note edit modal:', error);\r\n");
      out.write("            Swal.fire({\r\n");
      out.write("                icon: 'error',\r\n");
      out.write("                title: 'Error',\r\n");
      out.write("                text: 'Error opening edit form. Please try again.'\r\n");
      out.write("            });\r\n");
      out.write("        }\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    function deleteNote(id, type) {\r\n");
      out.write("        Swal.fire({\r\n");
      out.write("            title: 'Are you sure?',\r\n");
      out.write("            text: \"You won't be able to revert this!\",\r\n");
      out.write("            icon: 'warning',\r\n");
      out.write("            showCancelButton: true,\r\n");
      out.write("            confirmButtonColor: '#dc3545',\r\n");
      out.write("            cancelButtonColor: '#6c757d',\r\n");
      out.write("            confirmButtonText: 'Yes, delete it!'\r\n");
      out.write("        }).then((result) => {\r\n");
      out.write("            if (result.isConfirmed) {\r\n");
      out.write("                window.location.href = 'DeleteNoteServlet?id=' + id + '&noteType=' + type;\r\n");
      out.write("            }\r\n");
      out.write("        });\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    function resetRecordingUI() {\r\n");
      out.write("        document.getElementById('recordButton').disabled = false;\r\n");
      out.write("        document.getElementById('stopButton').disabled = true;\r\n");
      out.write("        document.getElementById('playButton').disabled = true;\r\n");
      out.write("        document.getElementById('recordingStatus').textContent = '';\r\n");
      out.write("        document.getElementById('recordedAudio').style.display = 'none';\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    // Voice recording functionality\r\n");
      out.write("    document.addEventListener('DOMContentLoaded', function() {\r\n");
      out.write("        const recordButton = document.getElementById('recordButton');\r\n");
      out.write("        const stopButton = document.getElementById('stopButton');\r\n");
      out.write("        const playButton = document.getElementById('playButton');\r\n");
      out.write("        const recordingStatus = document.getElementById('recordingStatus');\r\n");
      out.write("        const recordedAudio = document.getElementById('recordedAudio');\r\n");
      out.write("        const recordedAudioData = document.getElementById('recordedAudioData');\r\n");
      out.write("        \r\n");
      out.write("        if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {\r\n");
      out.write("            recordButton.addEventListener('click', startRecording);\r\n");
      out.write("            stopButton.addEventListener('click', stopRecording);\r\n");
      out.write("            playButton.addEventListener('click', playRecording);\r\n");
      out.write("        } else {\r\n");
      out.write("            recordButton.disabled = true;\r\n");
      out.write("            recordingStatus.textContent = 'Recording not supported in this browser';\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function startRecording() {\r\n");
      out.write("            recordingStatus.innerHTML = '<i class=\"fas fa-circle text-danger\"></i> Recording...';\r\n");
      out.write("            recordButton.disabled = true;\r\n");
      out.write("            stopButton.disabled = false;\r\n");
      out.write("            playButton.disabled = true;\r\n");
      out.write("            recordedAudio.style.display = 'none';\r\n");
      out.write("            \r\n");
      out.write("            navigator.mediaDevices.getUserMedia({ audio: true })\r\n");
      out.write("                .then(stream => {\r\n");
      out.write("                    mediaRecorder = new MediaRecorder(stream);\r\n");
      out.write("                    audioChunks = [];\r\n");
      out.write("                    \r\n");
      out.write("                    mediaRecorder.addEventListener('dataavailable', event => {\r\n");
      out.write("                        audioChunks.push(event.data);\r\n");
      out.write("                    });\r\n");
      out.write("                    \r\n");
      out.write("                    mediaRecorder.addEventListener('stop', () => {\r\n");
      out.write("                        audioBlob = new Blob(audioChunks, { type: 'audio/mpeg' });\r\n");
      out.write("                        audioUrl = URL.createObjectURL(audioBlob);\r\n");
      out.write("                        recordedAudio.src = audioUrl;\r\n");
      out.write("                        recordedAudio.style.display = 'block';\r\n");
      out.write("                        \r\n");
      out.write("                        // Convert to base64 for form submission\r\n");
      out.write("                        const reader = new FileReader();\r\n");
      out.write("                        reader.onloadend = function() {\r\n");
      out.write("                            recordedAudioData.value = reader.result;\r\n");
      out.write("                        };\r\n");
      out.write("                        reader.readAsDataURL(audioBlob);\r\n");
      out.write("                        \r\n");
      out.write("                        recordingStatus.innerHTML = '<i class=\"fas fa-check text-success\"></i> Recording saved';\r\n");
      out.write("                    });\r\n");
      out.write("                    \r\n");
      out.write("                    mediaRecorder.start();\r\n");
      out.write("                })\r\n");
      out.write("                .catch(error => {\r\n");
      out.write("                    console.error('Error accessing microphone:', error);\r\n");
      out.write("                    recordingStatus.innerHTML = '<i class=\"fas fa-exclamation-triangle text-warning\"></i> Error accessing microphone';\r\n");
      out.write("                    recordButton.disabled = false;\r\n");
      out.write("                });\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function stopRecording() {\r\n");
      out.write("            mediaRecorder.stop();\r\n");
      out.write("            recordButton.disabled = false;\r\n");
      out.write("            stopButton.disabled = true;\r\n");
      out.write("            playButton.disabled = false;\r\n");
      out.write("            \r\n");
      out.write("            // Stop all audio tracks\r\n");
      out.write("            mediaRecorder.stream.getTracks().forEach(track => track.stop());\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        function playRecording() {\r\n");
      out.write("            recordedAudio.play();\r\n");
      out.write("        }\r\n");
      out.write("        \r\n");
      out.write("        // Handle audio loading errors\r\n");
      out.write("        const audioElements = document.querySelectorAll('audio');\r\n");
      out.write("        audioElements.forEach(function(audio) {\r\n");
      out.write("            audio.addEventListener('error', function() {\r\n");
      out.write("                const errorMsg = document.createElement('div');\r\n");
      out.write("                errorMsg.className = 'alert alert-warning mt-2';\r\n");
      out.write("                errorMsg.innerHTML = '<small><i class=\"fas fa-exclamation-triangle\"></i> Audio file could not be loaded</small>';\r\n");
      out.write("                audio.parentNode.insertBefore(errorMsg, audio.nextSibling);\r\n");
      out.write("                audio.style.display = 'none';\r\n");
      out.write("            });\r\n");
      out.write("        });\r\n");
      out.write("    });\r\n");
      out.write("</script>\r\n");
