      out.write("function showAddTodoModal() {\r\n");
      out.write("    // Close the main add task modal if open\r\n");
      out.write("    const addTaskModal = bootstrap.Modal.getInstance(document.getElementById('addTaskModal'));\r\n");
      out.write("    if (addTaskModal) {\r\n");
      out.write("        addTaskModal.hide();\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    // Set default time\r\n");
      out.write("    const now = new Date();\r\n");
      out.write("    now.setMinutes(now.getMinutes() - now.getTimezoneOffset());\r\n");
      out.write("    document.getElementById('addTodoTime').value = now.toISOString().slice(0, 16);\r\n");
      out.write("    \r\n");
      out.write("    new bootstrap.Modal(document.getElementById('addTodoModal')).show();\r\n");
      out.write("}\r\n");
      out.write("\r\n");
      out.write("function showAddActivityModal() {\r\n");
      out.write("    // Close the main add task modal if open\r\n");
