      out.write("        addTaskModal.hide();\r\n");
      out.write("    }\r\n");
      out.write("    \r\n");
      out.write("    // Set default time\r\n");
      out.write("    const now = new Date();\r\n");
      out.write("    now.setMinutes(now.getMinutes() - now.getTimezoneOffset());\r\n");
      out.write("    document.getElementById('addActivityStartTime').value = now.toISOString().slice(0, 16);\r\n");
      out.write("    \r\n");
      out.write("    new bootstrap.Modal(document.getElementById('addActivityModal')).show();\r\n");
      out.write("}\r\n");
